import React, { useEffect } from 'react';
import './CourseMap.css';
import * as d3 from 'd3';
import { schemeSet2 } from 'd3-scale-chromatic';

const CourseMap = () => {
  useEffect(() => {
    renderd3("Core,AI/ML,Theory");
  }, []);

  const action = (group) => {
    const urlParams = new URLSearchParams(window.location.search);

    let groups = urlParams.get("groups");
    if (!groups) {
      groups = "Core";
    }
    groups = groups.split(",");

    const checkbox = document.getElementById(group);
    if (checkbox.checked) {
      groups.push(group);
    } else {
      const index = groups.indexOf(group);
      if (index > -1) {
        groups.splice(index, 1);
      }
    }

    groups = groups.join();

    if (window.history.pushState) {
      const newurl = `${window.location.origin}${window.location.pathname}?groups=${groups}`;
      window.history.replaceState({ path: newurl }, "", newurl);
    }

    window.location.reload();
  };

  const renderd3 = (groups) => {
    d3.json("/studentservices/course_guide_data?groups=" + groups, function(error, graph) {
        if (error) throw error;

        var link = svg
            .append("g")
                .attr("class", "links")
            .selectAll("line")
                .data(graph.links)
                .enter().append("line")
                    .attr("stroke-width", 2);

        var node = svg
            .append("g")
                .attr("class", "nodes")
            .selectAll("g")
                .data(graph.nodes)
                .enter().append("g");

        // Create the line after the nodes are rendered, dynamic to Node size
        svg.selectAll("line")
            .attr("marker-end", function (l) {
                    let found_node = node.filter((n) => (n.id == l.target && n.title));
                    if (found_node._groups[0].length > 0) {
                        return "url(#end_title)"
                    }
                    return "url(#end)";
                });

        var circles = node
            .append("circle")
                .attr("r", function(d) { return (d.title) ? title_circle_radius : circle_radius; })
                .attr("fill", function(d) { return color(d.group); })
                .attr("stroke", node_stroke)
                .attr("stroke-width", node_stroke_width);

        var simulation = d3.forceSimulation()
            .force("link", d3.forceLink().id(function(d) { return d.id; })
                                        .distance(200)
                                        .strength(function (l) {
                                            let found_node = graph.nodes.filter((n) => (n.id == l.source.id && n.title));
                                            let found_links = graph.links.filter(function(l2) {
                                                    if (l2.target.id == l.target.id) {
                                                        let found_node = graph.nodes.filter((n) => (n.id == l2.source.id && n.title));
                                                        return found_node.length == 1;
                                                    }
                                                    return false;
                                            });
                                            if (found_node.length == 1 && found_links.length == 1) {
                                                return 7;
                                            }
                                            return 2;
                                        }))
            .force("charge", d3.forceManyBody().strength(-200))
            .force("collision", d3.forceCollide().radius(function(d) { 
                return (d.title) ? title_circle_radius : circle_radius;
            }))
            .force("center", d3.forceY(height));

        function dragstarted(d) {
            if (!d3.event.active) simulation.alphaTarget(0.3).restart();

            d.old_fx = d.x;
            d.old_fy = d.y;

            d.fx = d.x;
            d.fy = d.y;
        }

        function dragged(d) {
            d.fx = d3.event.x;
            d.fy = d3.event.y;
        }

        function dragended(d) {
            if (!d3.event.active) simulation.alphaTarget(0);
            d.fx = null;
            d.fy = null;
            if (d.fixed) {
                d.fx = d.old_fx;
                d.fy = d.old_fy;
            }
        }

        // Create a drag handler and append it to the node object instead
        var drag_handler = d3.drag()
            .on("start", dragstarted)
            .on("drag", dragged)
            .on("end", dragended);

        drag_handler(node);

        var labels = node
            .append("a")
                .attr("xlink:href", function (d) { return d.link; })
                .attr("target", "_blank")
            .append("text")
                .text(function(d) { return d.id; })
                .attr("text-anchor", "middle")
                .attr("x", 0)
                .attr("y", 5);

        node.append("title")
            .text(function(d) { return d.id; });

        node.on("mouseover", function(d) {
            circles
                .filter(function(d2) { return d.group != d2.group; })
                .attr("opacity", 0.6);
            link
                .filter(function(l) {
                    return (l.source_group != d.group) && (l.target_group != d.group);
                })
                .attr("opacity", 0.1);
            labels
                .filter(function(d2) { return d.group != d2.group; })
                .attr("opacity", 0.5);
        });

        // Set the stroke width back to normal when mouse leaves the node.
        node.on("mouseout", function() {
            circles.attr("opacity", 1.0);
            link.attr("opacity", 1.0);
            labels.attr("opacity", 1.0);
        });

        svg.append("svg:defs").selectAll("marker")
            .data(["end", "end_title"])
            .enter().append("svg:marker")
                .attr("id", String)
                .attr("viewBox", "0 -5 10 10")
                .attr("refX", circle_radius + 10)
                .attr("refY", 0)
                .attr("markerWidth", 5)
                .attr("markerHeight", 5)
                .attr("orient", "auto")
            .append("svg:path")
                .attr("d", "M0,-5L10,0L0,5");
        svg.select("marker#end_title")
            .attr("refX", title_circle_radius + 10)

        simulation
            .nodes(graph.nodes)
            .on("tick", ticked);

        simulation.force("link")
            .links(graph.links);

        function inside_border(d, dim_size, d_coord) {
            let radius = (d.title) ? title_circle_radius : circle_radius;
            return Math.max(radius, Math.min(dim_size - radius, d_coord));
        };

        function ticked() {
            node
                .attr("cx", function(d) {
                    d.x = inside_border(d, width, d.x);
                    return d.x;
                })
                .attr("cy", function(d) {
                    d.y = inside_border(d, height, d.y);
                    if (d.group != 1) {
                        d.y = Math.max(d.y, 400);
                    }
                    return d.y;
                })
                .attr("transform", function(d) { return "translate(" + d.x + "," + d.y + ")"; });
            link
                .attr("x1", function(d) { return d.source.x; })
                .attr("y1", function(d) { return d.source.y; })
                .attr("x2", function(d) { return d.target.x; })
                .attr("y2", function(d) { return d.target.y; });
        }
    });
  };

  return (
    <div>
      <h2>Course Map</h2>
      <div className="group_selection">
        <input type="checkbox" id="Circuits" onChange={() => action('Circuits')} /> Circuits
        <input type="checkbox" id="Devices" onChange={() => action('Devices')} /> Devices
        <input type="checkbox" id="Signals/Control" onChange={() => action('Signals/Control')} /> Signals/Control
        <input type="checkbox" id="AI/ML" defaultChecked onChange={() => action('AI/ML')} /> AI/ML
        <input type="checkbox" id="Theory" defaultChecked onChange={() => action('Theory')} /> Theory
        <input type="checkbox" id="Graphics" onChange={() => action('Graphics')} /> Graphics
        <input type="checkbox" id="Design" onChange={() => action('Design')} /> Design
        <input type="checkbox" id="Architecture" onChange={() => action('Architecture')} /> Architecture
        <input type="checkbox" id="Systems" onChange={() => action('Systems')} /> Systems
      </div>

      <h2 style={{ textAlign: 'center' }}>
        Get a description of each EECS Academic Area here: <a href="https://www2.eecs.berkeley.edu/Research/Areas/">EECS Research Areas</a>
      </h2>

      <h2 style={{ textAlign: 'center' }}>
        <a href="/static/img/course-map-2019.png">Image version of the Course Guide</a>
      </h2>

      <div style={{ width: '100%', overflow: 'scroll' }}>
        <svg className="graph" width="2000" height="1200"></svg>
      </div>
    </div>
  );
};

export default CourseMap;
