import React, { useState } from 'react';
import './App.css';

// Example data: List of courses
const courses = [
  { id: 1, name: "Computer Science 101", time: "9:00 AM - 10:30 AM" },
  { id: 2, name: "Mathematics 204", time: "11:00 AM - 12:30 PM" },
  { id: 3, name: "Physics 309", time: "2:00 PM - 3:30 PM" }
];

function App() {
  const [schedule, setSchedule] = useState([]);

  // Function to add or remove course from the schedule
  const toggleCourse = (course) => {
    if (schedule.find(c => c.id === course.id)) {
      setSchedule(schedule.filter(c => c.id !== course.id));
    } else {
      setSchedule([...schedule, course]);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Class Scheduler</h1>
        <div className="course-list">
          {courses.map(course => (
            <div key={course.id} className="course" onClick={() => toggleCourse(course)}>
              <h2>{course.name}</h2>
              <p>{course.time}</p>
            </div>
          ))}
        </div>
        <h2>Your Schedule</h2>
        <div className="schedule">
          {schedule.map(course => (
            <div key={course.id}>
              <h3>{course.name}</h3>
              <p>{course.time}</p>
            </div>
          ))}
        </div>
      </header>
    </div>
  );
}

export default App;
