import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

// Example data: List of courses
const courses = [
  { id: 1, name: "CS 189", time: "9:00 AM - 10:30 AM" },
  { id: 2, name: "Math 104", time: "11:00 AM - 12:30 PM" },
  { id: 3, name: "Physics 137A", time: "2:00 PM - 3:30 PM" }
];

function App() {
  const [schedule, setSchedule] = useState([]);
  const [input, setInput] = useState('');
  const [reply, setReply] = useState('');

  // Function to add or remove course from the schedule
  const toggleCourse = (course) => {
    if (schedule.find(c => c.id === course.id)) {
      setSchedule(schedule.filter(c => c.id !== course.id));
    } else {
      setSchedule([...schedule, course]);
    }
  };

  // Function to handle chatbot input and send request to the Python script
  const handleChatbot = async () => {
    try {
      const response = await axios.post('http://localhost:5000/chatbot', { input: input });
      setReply(response.data.reply);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Class Scheduler</h1>
        <div className="course-list">
          {courses.map(course => (
            <div key={course.id} className="course" onClick={() => toggleCourse(course)}>
              <h2>{course.name}</h2>
              <p>{course.time}</p>
            </div>
          ))}
        </div>
        <h2>Your Schedule</h2>
        <div className="schedule">
          {schedule.map(course => (
            <div key={course.id}>
              <h3>{course.name}</h3>
              <p>{course.time}</p>
            </div>
          ))}
        </div>
        <h2>Chatbot</h2>
        <div className="chatbot">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask a question..."
          />
          <button onClick={handleChatbot}>Send</button>
          <p>{reply}</p>
        </div>
      </header>
    </div>
  );
}

export default App;
